mapper.update:
update bd_bet_record
set
  multiple = #{multiple,jdbcType=DOUBLE},
  escape_time = #{escapeTime,jdbcType=VARCHAR},
  profit = #{profit,jdbcType=DOUBLE},
  escape_status = #{escapeStatus,jdbcType=VARCHAR}
where issue_no = #{issueNo,jdbcType=BIGINT}
and player_id = #{playerId,jdbcType=INTEGER}

update.foreach:
update bd_bet_record
profit = #{item.profit}, multiple = #{item.multiple}, bet_status = #{item.betStatus}, escape_status = #{item.escapeStatus}, escape_time = #{item.escapteTime}
where issue_no = #{item.issueNo} and player_id = #{item.id}
